CREATE TABLE Imagenes (
	id_imagen INT PRIMARY KEY IDENTITY(1,1),
	url_imagen varbinary(max),
	id_producto INT FOREIGN KEY REFERENCES Productos(id_producto)
);



-- Tabla de Etiquetas
CREATE TABLE Etiquetas (
    id_etiqueta INT PRIMARY KEY IDENTITY(1,1),
    nombre_etiqueta NVARCHAR(100) NOT NULL
);

-- Tabla relación Producto - Etiqueta
CREATE TABLE ProductoEtiqueta (
    id_producto INT FOREIGN KEY REFERENCES Productos(id_producto),
    id_etiqueta INT FOREIGN KEY REFERENCES Etiquetas(id_etiqueta),
    PRIMARY KEY (id_producto, id_etiqueta)
);

-- Tabla de Reseñas
CREATE TABLE Resenas (
    id_usuario INT NOT NULL,
    id_producto INT NOT NULL,
    fecha DATETIME DEFAULT GETDATE(),
    comentario NVARCHAR(MAX),
    valoracion INT CHECK(valoracion BETWEEN 1 AND 5),
    estado BIT DEFAULT 1,
    PRIMARY KEY (id_usuario, id_producto),
    FOREIGN KEY (id_usuario) REFERENCES Usuarios(id_usuario),
    FOREIGN KEY (id_producto) REFERENCES Productos(id_producto)
);

-- Tabla de Promociones
CREATE TABLE Promociones (
    id_promocion INT PRIMARY KEY IDENTITY(1,1),
    tipo_promocion NVARCHAR(20) CHECK (tipo_promocion IN ('producto', 'categoria')),
    id_producto INT NULL,
    id_categoria INT NULL,
    descuento DECIMAL(5,2) NOT NULL,
    fecha_inicio DATE,
    fecha_fin DATE,
	FOREIGN KEY (id_producto) REFERENCES Productos(id_producto),
	FOREIGN KEY (id_categoria) REFERENCES Categorias(id_categoria)
);

-- Tabla de Carrito
CREATE TABLE Carrito (
    id_carrito INT PRIMARY KEY IDENTITY(1,1),
    id_usuario INT FOREIGN KEY REFERENCES Usuarios(id_usuario)
);

-- Tabla Detalle del Carrito
CREATE TABLE DetalleCarrito (
    id_carrito INT FOREIGN KEY REFERENCES Carrito(id_carrito),
    id_producto INT FOREIGN KEY REFERENCES Productos(id_producto),
    cantidad INT NOT NULL,
    PRIMARY KEY (id_carrito, id_producto)
);

-- Tabla de Pagos
CREATE TABLE Pagos (
    id_pago INT PRIMARY KEY IDENTITY(1,1),
    metodo_pago NVARCHAR(50),
    vuelto INT,
    costo_envio DECIMAL(10,2),
    direccion_entrega NVARCHAR(MAX),
    fecha_pago DATE
);

-- Tabla de Estados del Pedido
CREATE TABLE Estados (
    id_estado INT PRIMARY KEY IDENTITY(1,1),
    nombre_estado NVARCHAR(50) NOT NULL
);

-- Tabla de Pedidos
CREATE TABLE Pedidos (
    id_pedido INT PRIMARY KEY IDENTITY(1,1),
    id_cliente INT FOREIGN KEY REFERENCES Usuarios(id_usuario),
    metodo_entrega NVARCHAR(50),
    fecha_pedido DATETIME DEFAULT GETDATE(),
    direccion_envio NVARCHAR(MAX),
    id_estado INT FOREIGN KEY REFERENCES Estados(id_estado),
    id_pago INT FOREIGN KEY REFERENCES Pagos(id_pago)
);


-- Tabla de Detalle Pedido
CREATE TABLE DetallePedido (
    id_detalle INT PRIMARY KEY IDENTITY(1,1),
    id_pedido INT FOREIGN KEY REFERENCES Pedidos(id_pedido),
    subtotal DECIMAL(10,2),
    impuesto DECIMAL(10,2),
    total_con_impuesto DECIMAL(10,2)
);

-- Tabla de Detalle Producto Pedido
CREATE TABLE DetallePedidoProducto (
    id_pedido INT FOREIGN KEY REFERENCES Pedidos(id_pedido),
    id_producto INT FOREIGN KEY REFERENCES Productos(id_producto),
    cantidad INT,
    PRIMARY KEY (id_pedido, id_producto)
);


